typedef enum {
    ATK_TEXT_CLIP_NONE,
    ATK_TEXT_CLIP_MIN,
    ATK_TEXT_CLIP_MAX,
    ATK_TEXT_CLIP_BOTH
} AtkTextClipType;

typedef struct {
  AtkTextRectangle bounds;
  gint start_offset;
  gint end_offset;
  gchar* content;
} AtkTextRange;

typedef struct {
  gint x;
  gint y;
  gint width;
  gint height;
} AtkTextRectangle;

typedef struct {
  gchar* name;
  gchar* value;
} AtkAttribute;

