example.programlisting:
gdk_error_trap_push ();

/* ... Call the X function which may cause an error here ... */

/* Flush the X queue to catch errors now. */
gdk_flush ();

if (gdk_error_trap_pop ())
  {
    /* ... Handle the error here ... */
  }
