typedef enum
{
  GTK_ICON_LOOKUP_NO_SVG = 1 << 0,
  GTK_ICON_LOOKUP_FORCE_SVG = 1 << 1,
  GTK_ICON_LOOKUP_USE_BUILTIN = 1 << 2
} GtkIconLookupFlags;

void        gtk_icon_theme_set_search_path  (GtkIconTheme *icon_theme,
                                             const gchar *path[],
                                             gint n_elements);

