struct GtkTableChild {

  GtkWidget *widget;
  guint16 left_attach;
  guint16 right_attach;
  guint16 top_attach;
  guint16 bottom_attach;
  guint16 xpadding;
  guint16 ypadding;
  guint xexpand : 1;
  guint yexpand : 1;
  guint xshrink : 1;
  guint yshrink : 1;
  guint xfill : 1;
  guint yfill : 1;
};

struct GtkTableRowCol {

  guint16 requisition;
  guint16 allocation;
  guint16 spacing;
  guint need_expand : 1;
  guint need_shrink : 1;
  guint expand : 1;
  guint shrink : 1;
  guint empty : 1;
};

GtkWidget*  gtk_table_new                   (guint rows,
                                             guint columns,
                                             gboolean homogeneous);

void        gtk_table_resize                (GtkTable *table,
                                             guint rows,
                                             guint columns);

void        gtk_table_set_row_spacing       (GtkTable *table,
                                             guint row,
                                             guint spacing);

void        gtk_table_set_col_spacing       (GtkTable *table,
                                             guint column,
                                             guint spacing);
